INSERT INTO `setting` (`id_setting`, `Nama`, `gambar_header`, `kunci`, `addsense`, `deskripsi`, `fans_page`, `map_google`, `verfication`, `favicon`, `logo`, `alamat`, `telepone`, `email`, `nama_rektor`, `video_profil`, `tentang_universitas`) VALUES
(1, 'Fakultas Ekonomi Dan Bisnis ', 'file_1546414526.png', 'universitas ekaskati padang,universitas ,ekasakti,unes,unes,unesuniversitas ekaskati padang,universitas ,ekasakti,unes,unes,unesuniversitas ekaskati padang,universitas ,ekasakti,unes,unes,unesuniversitas ekaskati padang,universitas ,ekasakti,unes,unes,unesuniversitas ekaskati padang,universitas ,ekasakti,unes,unes,unesuniversitas ekaskati padang,universitas ,ekasakti,unes,unes,unesuniversitas ekaskati padang,universitas ,ekasakti,unes,unes,unes', ' ', 'Assalamualaikum Wr. Wb.\r\n\r\nSalam Khidmat Untuk Kita Semua!\r\n\r\nSeiring pesatnya perkembangan teknologi informasi dan komunikasi, menuntut adanya penyesuaian di pelbagai aspek kehidupan, termasuk dunia pendidikan, dalam hal ini adalah perguruan tinggi. Pemanfaatan teknologi informasi dan komunikasi secara konstruktif diarahkan dalam upaya untuk mempermudah, mempercepat, dan memperluas jangkauan komunikasi, khususnya dalam hal proses belajar mengajar.\r\n\r\nTentunya, STAI ASIQ terus melakukan penyesuaian dan pembaruan untuk tetap mengukuhkan jadi diri sebagai Pioner Perubahan. Kami menyadari sebagai Perguruan Tinggi yang lebih dulu di Kabupaten OKI Sumatera Selatan, kualitas adalah kunci agar tetap mampu bersaing dan meraih kepercayaan publik. Kami terus mengembangkan diri dan melangkah maju ke arah perubahan yang dinamis, untuk itu, semboyan kami adalah  Cerdas, Profesional dan Berkhidmat Untuk Umat\r\n\r\nDi awal tahun 2017 merupakan momentum yang tepat bagi STAI As-Shiddiqiyah untuk menghadirkan website dengan desain baru.  Dengan lebih hidup, inovatif dan menarik, serta memudahkan dalam mengakses informasi secara lebih cepat, yang disajikan dalam dua bahasa/bilingual (Indonesia dan Inggris).\r\n\r\nSelain sebagai bentuk jawaban atas tantangan globalisasi dalam penggunaan teknologi informatika, wajah baru website STAI As-Shiddiqiyah diharapkan dapat lebih mendukung kelancaran proses belajar mengajar di lingkungan STAI As-Shiddiqiyah, serta dapat meningkatkan mutu pelayanan yang lebih baik. Besar harapan kita bersama, kehadiran wajah baru website STAI As-Shiddiqiyah ini dapat memberikan manfaat tidak hanya bagi seluruh sivitas akademika STAI As-Shiddiqiyah, namun juga bagi masyarakat, bangsa dan negara. Akhir kata kami mengharapkan doa dan dukungan masyarakat agar STAI As-Shiddiqiyah tetap menjadi Sekolah Tinggi Agama Islam terbaik dan menjadi kebanggaan masyarakat, bangsa dan negara.\r\n\r\nSalam Silaturahim Kami,\r\n\r\n \r\n\r\nDr. AGUS SHOLIKHIN, S.Si, M.Pd.I\r\n\r\nKetua STAI As-Shiddiqiyah OKI', ' ', '&lt;iframe src=\"https://www.google.com/maps/embed?pb=!1m23!1m12!1m3!1d127657.1286321205!2d100.28467628632635!3d-0.9365913003229184!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!4m8!3e6!4m0!4m5!1s0x2fd4b931f88a82b5:0x71b996adc979f60f!2sunes+padang!3m2!1d-0.936592!2d100.3547169!5e0!3m2!1sid!2sid!4v1531977366555\" width=\"100%\" height=\"400\" frameborder=\"0\" style=\"border:0\" allowfullscreen&gt;&lt;/iframe&gt;', '&lt;meta name=\"google-site-verification\" content=\"JIHRw5PfOZsy7jLzAjqN73ZUzg2lch6wf5WYdh2WjJI\" /&gt;\r\n      ', 'logo.png', 'logo1.png', ' Jl . Veteran Dalam No.26B, Padang', ' (0751) 28859', 'kampusku@kampusku.com', 'Dr. Muzadi Ishaq amin', 'i2Iprn5jPjI', 'Pangabean');

